import React, { Component } from 'react';
import './scss/App.scss';

export class Slide4 extends Component {
  constructor(props){
    super(props);
    this.state={
      blocksVal: 0,
      reboundsVal: 0,
      stealsVal: 0,
    }
  }
  render(){
return(
      <div className="slide4"> 
      <h1 className="headline">YOUR STATS PER GAME</h1>
      <h2>DEFENCE:</h2>
      <div className="statsContainer">
      <div>
        <span>BLOCKS</span>
        <input value={this.state.blocksVal} type="number" min="0" step="1"></input>
       </div>
      <div>
        <span>REBOUNDS</span>
        <input value={this.state.reboundsVal} type="number" min="0" step="1"></input>
      </div>
     <div>
       <span>STEALS</span>
       <input value={this.state.stealsVal} type="number" min="0" step="1"></input>
      </div> 
     </div>
</div>
    )
  }
}

export default Slide4;
