import React, { Component } from 'react';
import './scss/App.scss';
import butler from "./images/butler.jpeg";
import curry from "./images/curry.jpg";
import damian from "./images/damian.jpg";
import davis from"./images/davis.jpg";
import deroz from"./images/deroz.jpg";
import dirk from "./images/dirk.jpg";
import gortat from"./images/gortat.jpg";
import harden from"./images/harden.jpg";
import jj from "./images/jj.jpg";
import kawhi from"./images/kawhi.jpg";
import kyrie from "./images/kyrie.jpg";
import lebron from "./images/lebron.jpg";
import rondo from "./images/rondo.jpg";
import rose from "./images/rose.jpg";
import russ from "./images/russ.jpg";
import vince from "./images/vince.jpg";

export class Slide2 extends Component {
  constructor(props){
    super(props);
    this.state={
      players: [
        {butler},
        {curry},
        {damian},
        {davis},
        {deroz},
        {dirk},
        {gortat},
        {harden},
        {jj},
        {kawhi},
        {kyrie},
        {lebron},
        {rondo},
        {rose},
        {russ},
        {vince},

      ]
    }
  }


  render(){
    return(
      <div className="slide2">  
        <h1 className="headline">CHOOSE THE PLAYER:</h1>
        <div id="playersContainer">
        {
          this.state.players.map((picture, index) => {
            return (
              <div style={{backgroundImage: `url(${picture})`}} className="playerBox" key={index} ></div>
            )
          })
        }

        </div>
      </div>
    )
  }
}

export default Slide2;
