import React, { Component } from 'react';
import './scss/App.scss';


export class Slide3 extends Component {
  constructor(props){
    super(props);
    this.state={
      pointsVal: 0,
      threePointersVal: 0,
      freeThrowsVal: 0,
      assistsVal:0,
    }
  }

getPoints =(event) =>{
  this.setState({
    pointsVal: event.target.value,
  })

}

getThrees = (event) =>{
  this.setState({
    threePointersVal: event.target.value,
  })
}

getFree =(event) =>{
  this.setState({
    freeThrowsVal: event.target.value,
  })
}

getAssists = (event) =>{
  this.setState({
    assistsVal: event.target.value,
  })
}

  render(){
return(
      <div className="slide3"> 
       <h1 className="headline">YOUR STATS PER GAME</h1>
       <h2>ATTACK:</h2>
       <div className="statsContainer">
         <div className="inputContainer">
          <span>POINTS</span>
          <input  type="number" value={this.state.pointsVal} min="0" step="1" onChange={this.getPoints}></input>
        </div>
      <div className="inputContainer">
        <span>THREE POINTERS</span>
        <input  type="number" value={this.state.threePointersVal} min="0" step="1"onChange={this.getThrees}></input>
       </div>
     <div className="inputContainer">
       <span> FREE THROWS</span>
       <input type="number" value={this.state.freeThrowsVal} min="0" step="1"onChange={this.getFree}></input>
      </div> 
     <div className="inputContainer">
       <span>ASSISTS</span>
       <input type="number" value={this.state.assistsVal} min="0" step="1"onChange={this.getAssists}></input>
      </div> 
     </div>

      </div>
    )
  }
}

export default Slide3;
