import React, { Component } from 'react';
import './scss/App.scss';


export class Slide3 extends Component {
  constructor(props){
    super(props);
    this.state={
      pointsVal: 0,
      threePointersVal: 0,
      freeThrowsVal: 0,
      assistsVal:0,
    }

  }
  render(){
return(
      <div className="slide3"> 
       <h1 className="headline">YOUR STATS PER GAME</h1>
       <h2>ATTACK:</h2>
       <div className="statsContainer">
         <div className="inputContainer">
          <span>POINTS</span>
          <input  type="number" value={this.state.pointsVal} min="0" step="1"></input>
        </div>
      <div className="inputContainer">
        <span>THREE POINTERS</span>
        <input  type="number" value={this.state.threePointersVal} min="0" step="1"></input>
       </div>
     <div className="inputContainer">
       <span> FREE THROWS</span>
       <input type="number" value={this.state.freeThrowsVal} min="0" step="1"></input>
      </div> 
     <div className="inputContainer">
       <span>ASSISTS</span>
       <input type="number" value={this.state.assistsVal} min="0" step="1"></input>
      </div> 
     </div>

      </div>
    )
  }
}

export default Slide3;
