import Chart from 'react-google-charts';
import React, { Component } from 'react';
import './scss/App.scss';


export class Slide5 extends Component {
  constructor(props){
    super(props);
    this.state={
      playerID: props.playerID,
      playerName: props.playerName,
      pointsVal: 0,
      threePointersVal: 0,
      freeThrowsVal: 0,
      assistsVal:0,
      blocksVal: 0,
      reboundsVal: 0,
      stealsVal: 0,
      playersPointsVal: 0,
      playersThreePointersVal: 0,
      playersFreeThrowsVal: 0,
      playersAssistsVal:0,
      playersBlocksVal: 0,
      playersReboundsVal: 0,
      playersStealsVal: 0,
      show:false,
    }
  }
  componentDidMount() {
     fetch(`https://stats.nba.com/stats/playercareerstats/?PerMode=PerGame&PlayerID=1713`)
       .then(response => response.json())
       .then(player => {
         console.log(player)
          this.setState({
            playersPointsVal: player.resultSets[1].rowSet[0][23]
          })
          console.log(this.state.playersPointsVal)

       })
       .catch(() => {
         console.log("error")
       })
   }
  componentWillReceiveProps(nextProps) {
    console.log(this.state.pointsVal)
    this.setState({
      playerName: nextProps.playerName,
      pointsVal: nextProps.pointsVal,
      threePointersVal: nextProps.threePointersVal,
      freeThrowsVal: nextProps.freeThrowsVal,
      assistsVal: nextProps.assistsVal,
      blocksVal: nextProps.blocksVal,
      reboundsVal: nextProps.reboundsVal,
      stealsVal: nextProps.stealsVal,
    })
  }

  show = ()=>{
    this.setState({
      show: true,
    })
  }

  render(){
    const chartSize= "100%";
    return(
      <div className="slide5">  
        <h1 className="headline">YOUR RESULTS:</h1>
    { this.state.show === false && <button className="showBtn" onClick={this.show}>SHOW MY RESULTS</button> }
        {this.state.show===true && <Chart
        width={chartSize}
        height={chartSize}
        chartType="ColumnChart"
        loader={<div>Loading Chart</div>}
    data={[
      ['Player', 'You', this.state.playerName],
      ['Points', this.state.pointsVal, this.state.playersPointsVal],
      ['Three Pointers', this.state.threePointersVal, this.state.playersThreePointersVal],
      ['Free Throws', this.state.freeThrowsVal, this.state.playersThreePointersVal],
      ['Assists', this.state.assistsVal, this.state.playersAssistsVal],
      ['Blocks', this.state.blocksVal, this.state.playersBlocksVal],
      ['Steals', this.state.stealsVal, this.state.playersStealsVal],
      ['Rebounds', this.state.reboundsVal, this.state.playersReboundsVal],
    ]}
    options={{
      title: 'Your Comparison',
      chartArea: { width: '70%' },
      hAxis: {
        title: 'Stats',
        minValue: 0,
      },
      vAxis: {
        title: 'Player',
      },
      animation: {
        startup: true,
        easing: 'linear',
        duration: 1500,
      },
    }}
        />
  }
      </div>

    )
  }
}

export default Slide5;
