import React, { Component } from 'react';
import './scss/App.scss';


export class Slide2 extends Component {
  constructor(props){
    super(props);
    this.state={
      players: [
        "bball.jpg",
        "./images/curry.jpg",
        "./images/damian.jpg",
        "./images/davis.jpg",
        "./images/deroz.jpg",
        "./images/dirk.jpg",
        "./images/gortat.jpg",
        "./images/harden.jpg",
        "./images/jj.jpg",
        "./images/kawhi.jpg",
        "./images/kyrie.jpg",
        "./images/lebron.jpg",
        "./images/rondo.jpg",
        "./images/rose.jpg",
        "./images/russ.jpg",
        "./images/vince.jpg",
      ]
    }
  }


  render(){
    return(
      <div className="slide2">  
        <h1 className="headline">CHOOSE THE PLAYER:</h1>
        <div id="playersContainer">
        {
          this.state.players.map((picture, index) => {
            return (
              <div style={{backgroundImage: `url(${picture})`}} className="playerBox" key={index} ></div>
            )
          })
        }

        </div>
      </div>
    )
  }
}

export default Slide2;
