import React, { Component } from 'react';
import './scss/App.scss';
import Slide1 from './slide1';
import Slide2 from './slide2';
import Slide3 from './slide3';
import Slide4 from './slide4';
import Slide5 from './slide5';


 export class Slide extends Component {
  constructor(props){
    super(props);
    this.state={
      translateValue: props.translateValue,
      chosenPlayerID:"",
      chosenPlayerName:"",
      pointsVal: 0,
      threePointersVal: 0,
      freeThrowsVal: 0,
      assistsVal:0,
      blocksVal: 0,
      reboundsVal: 0,
      stealsVal: 0,
      }
    }

    componentWillReceiveProps(nextProps) {
      this.setState({translateValue: nextProps.translateValue})
    }

    setID=(index)=>{
      console.log(this.state.chosenPlayerID)
      if(index===0){
      this.setState({
      chosenPlayerID: "202710",
      chosenPlayerName:"Jimmy Butler"
      }) 
    } else if (index===1){
      this.setState({
        chosenPlayerID: "201939",
        chosenPlayerName:"Stephen Curry"
        }) 
    }else if (index===2){
      this.setState({
        chosenPlayerID: "203081",
        chosenPlayerName:"Damian Lillard"
        }) 
    }else if (index===3){
      this.setState({
        chosenPlayerID: "203076",
        chosenPlayerName:"Anthony Davis"
        }) 
    }else if (index===4){
      this.setState({
        chosenPlayerID: "201942",
        chosenPlayerName:"DeMar DeRozan"
        }) 
    }else if (index===5){
      this.setState({
        chosenPlayerID: "1717",
        chosenPlayerName:"Dirk Novitzky"
        }) 
    }else if (index===6){
      this.setState({
        chosenPlayerID: "101162",
        chosenPlayerName:"Marcin Gortat"
        }) 
    }else if (index===7){
      this.setState({
        chosenPlayerID: "200755",
        chosenPlayerName:"James Harden"
        }) 
    }else if (index===8){
      this.setState({
        chosenPlayerID: "202695",
        chosenPlayerName:"JJ Redick"
        }) 
    }else if (index===9){
      this.setState({
        chosenPlayerID: "201939",
        chosenPlayerName:"Kawhi Leonard"
        }) 
    } else if (index===10){
      this.setState({
        chosenPlayerID: "202681",
        chosenPlayerName:"Kyrie Irving"
        }) 
    }else if (index===11){
      this.setState({
        chosenPlayerID: "2544",
        chosenPlayerName:"LeBron James"
        }) 
       } else if (index===12){
      this.setState({
        chosenPlayerID: "200765",
        chosenPlayerName:"Rajon Rondo"
        }) 
    }else if (index===13){
      this.setState({
        chosenPlayerID: "201565",
        chosenPlayerName:"Derrick Rose"
        }) 
    }else if (index===14){
      this.setState({
        chosenPlayerID: "201566",
        chosenPlayerName:"Russel Westbrook"
        }) 
    }else if (index===15){
      this.setState({
        chosenPlayerID: "1713",
        chosenPlayerName:"Vince Carter"
        }) 
    } else {
        this.setState({
          chosenPlayerID: ""
          }) 
      }
    }

    getPoints =(event) =>{
      this.setState({
        pointsVal: parseInt(event.target.value),
      })

    }

    getThrees = (event) =>{
      this.setState({
        threePointersVal: parseInt(event.target.value),
      })
    }

    getFree =(event) =>{
      this.setState({
        freeThrowsVal: parseInt(event.target.value),
      })
    }

    getAssists = (event) =>{
      this.setState({
        assistsVal: parseInt(event.target.value),
      })
    }
    getBlocks=(event)=>{
      this.setState({
        blocksVal: parseInt(event.target.value),
      })
    }
    getRebs=(event)=>{
      this.setState({
        reboundsVal: parseInt(event.target.value),
      })
    }
    getSteals=(event)=>{
      this.setState({
        stealsVal: parseInt(event.target.value),
      })
    }


  render(){

      return <div className="slide" style={{transform: `translateX(${this.state.translateValue}px)`, transition: 'transform ease-out 0.45s'}} >
        <Slide1/>
        <Slide2 setID={this.setID}/>
        <Slide3 getPoints={this.getPoints} getFree={this.getFree} getThrees={this.getThrees} getAssists={this.getAssists}
        freeThrowsVal={this.state.freeThrowsVal} pointsVal= {this.state.pointsVal} threePointersVal={this.state.threePointersVal} 
        assistsVal={this.state.assistsVal}
        />
        <Slide4 getBlocks={this.getBlocks} getRebs={this.getRebs} getSteals={this.getSteals}
              blocksVal={this.state.blocksVal} reboundsVal={this.state.reboundsVal} stealsVal={this.state.stealsVal} />
        <Slide5 playerName={this.state.chosenPlayerName} platersID={this.state.chosenPlayerID}
        blocksVal={this.state.blocksVal} reboundsVal={this.state.reboundsVal} stealsVal={this.state.stealsVal}
        freeThrowsVal={this.state.freeThrowsVal} pointsVal= {this.state.pointsVal} threePointersVal={this.state.threePointersVal} 
        assistsVal={this.state.assistsVal}
        />
        </div>    
  }
 }

export default Slide;
