import Chart from 'react-google-charts';

import React, { Component } from 'react';
import './scss/App.scss';

export class Slide5 extends Component {
  constructor(props){
    super(props);
    this.state={
      playerName: props.playerName,
      pointsVal: 0,
      threePointersVal: 0,
      freeThrowsVal: 0,
      assistsVal:0,
      blocksVal: 0,
      reboundsVal: 0,
      stealsVal: 0,
      playersPointsVal: 0,
      playersThreePointersVal: 0,
      playersFreeThrowsVal: 0,
      playersAssistsVal:0,
      playersBlocksVal: 0,
      playersReboundsVal: 0,
      playersStealsVal: 0,
    }
  }

  componentWillReceiveProps(nextProps) {
    this.setState({playerName: nextProps.playerName})
  }

  render(){
    const chartSize= "100%";
return(
      <div className="slide5">  
        <h1 className="headline">YOUR RESULTS:</h1>
        <Chart
        width={chartSize}
        height={chartSize}
        chartType="ColumnChart"
        loader={<div>Loading Chart</div>}
    data={[
      ['Player', 'You', 'NBA'],
      ['Points', 8175000, 8008000],
      ['Three Pointers', 3792000, 3694000],
      ['Free Throws', 2695000, 2896000],
      ['Assists', 2099000, 1953000],
      ['Blocks', 1526000, 1517000],
      ['Steals', 1526000, 1517000],
      ['Rebounds', 1526000, 1517000],
    ]}
    options={{
      title: 'Your Comparison',
      chartArea: { width: '70%' },
      hAxis: {
        title: 'Stats',
        minValue: 0,
      },
      vAxis: {
        title: 'Player',
      },
      animation: {
        startup: true,
        easing: 'linear',
        duration: 1500,
      },
    }}

        />
      </div>
    )
  }
}

export default Slide5;
