import React, { Component } from 'react';
import './scss/App.scss';
import Slide1 from './slide1';
import Slide2 from './slide2';
import Slide3 from './slide3';
import Slide4 from './slide4';
import Slide5 from './slide5';


 export class Slide extends Component {
  constructor(props){
    super(props);
    this.state={
      translateValue: props.translateValue,
      chosenPlayerID:"",
      pointsVal: 0,
      threePointersVal: 0,
      freeThrowsVal: 0,
      assistsVal:0,
      blocksVal: 0,
      reboundsVal: 0,
      stealsVal: 0,
      playersPointsVal: 0,
      playersThreePointersVal: 0,
      playersFreeThrowsVal: 0,
      playersAssistsVal:0,
      playersBlocksVal: 0,
      playersReboundsVal: 0,
      playersStealsVal: 0,
      }
    }

    componentWillReceiveProps(nextProps) {
      this.setState({translateValue: nextProps.translateValue})
    }

    setID=(index)=>{
      console.log(this.state.chosenPlayerID)
      if(index===0){
      this.setState({
      chosenPlayerID: "202710"
      }) 
    } else if (index===1){
      this.setState({
        chosenPlayerID: "201939"
        }) 
    }else if (index===2){
      this.setState({
        chosenPlayerID: "203081"
        }) 
    }else if (index===3){
      this.setState({
        chosenPlayerID: "203076"
        }) 
    }else if (index===4){
      this.setState({
        chosenPlayerID: "201942"
        }) 
    }else if (index===5){
      this.setState({
        chosenPlayerID: "1717"
        }) 
    }else if (index===6){
      this.setState({
        chosenPlayerID: "101162"
        }) 
    }else if (index===7){
      this.setState({
        chosenPlayerID: "200755"
        }) 
    }else if (index===8){
      this.setState({
        chosenPlayerID: "202695"
        }) 
    }else if (index===9){
      this.setState({
        chosenPlayerID: "201939"
        }) 
    } else if (index===10){
      this.setState({
        chosenPlayerID: "202681"
        }) 
    }else if (index===11){
      this.setState({
        chosenPlayerID: "2544"
        }) 
       } else if (index===12){
      this.setState({
        chosenPlayerID: "200765"
        }) 
    }else if (index===13){
      this.setState({
        chosenPlayerID: "201565"
        }) 
    }else if (index===14){
      this.setState({
        chosenPlayerID: "201566"
        }) 
    }else if (index===15){
      this.setState({
        chosenPlayerID: "1713"
        }) 
    } else {
        this.setState({
          chosenPlayerID: ""
          }) 
      }
    }

    getPoints =(event) =>{
      this.setState({
        pointsVal: event.target.value,
      })
      console.log(this.state.pointsVal)

    }

    getThrees = (event) =>{
      this.setState({
        threePointersVal: event.target.value,
      })
      console.log(this.state.threePointersVal)
    }

    getFree =(event) =>{
      this.setState({
        freeThrowsVal: event.target.value,
      })
      console.log(this.state.freeThrowsVal)
    }

    getAssists = (event) =>{
      this.setState({
        assistsVal: event.target.value,
      })
      console.log(this.state.assistsVal)
    }
    getBlocks=(event)=>{
      this.setState({
        blocksVal: event.target.value,
      })
      console.log(this.state.blocksVal)
    }
    getRebs=(event)=>{
      this.setState({
        reboundsVal: event.target.value,
      })
      console.log(this.state.blocksVal)
    }
    getSteals=(event)=>{
      this.setState({
        stealsVal: event.target.value,
      })
      console.log(this.state.blocksVal)
    }


  render(){

      return <div className="slide" style={{transform: `translateX(${this.state.translateValue}px)`, transition: 'transform ease-out 0.45s'}} >
        <Slide1/>
        <Slide2 setID={this.setID}/>
        <Slide3 getPoints={this.getPoints} getFree={this.getFree} getThrees={this.getThrees} getAssists={this.getAssists}
        freeThrowsVal={this.state.freeThrowsVal} pointsVal= {this.state.pointsVal} threePointersVal={this.state.threePointersVal} 
        assistsVal={this.state.assistsVal}
        />
        <Slide4 getBlocks={this.getBlocks} getRebs={this.getRebs} getSteals={this.getSteals}
              blocksVal={this.state.blocksVal} reboundsVal={this.state.reboundsVal} stealsVal={this.state.stealsVal} />
        <Slide5/>
        </div>    
  }
 }

export default Slide;
