import React, { Component } from 'react';
import './scss/App.scss';
import Button from './button';
import PrevButton from './prevbutton'
import Slide from './slide';

export class Slider extends Component {
  constructor(props){
    super(props);
    this.state={
      currentIndex: 0,
      translateValue: 0,
      buttonTxt: "FOR-WARD!",
      buttonDispl: "none",
    }
  }



  slideWidth =()=> {
    return document.querySelector('.slider').clientWidth
 }

 prevSlide =()=> {
  if(this.state.currentIndex > 0)
  this.setState(prevState => ({
    currentIndex: prevState.currentIndex - 1,
    translateValue: prevState.translateValue + this.slideWidth(),
    buttonDispl: "inline"
  }))
}

  nextSlide=()=>{
    if(this.state.currentIndex < 4){
     this.setState(prevState => ({
       currentIndex: prevState.currentIndex + 1,
       translateValue: prevState.translateValue + -(this.slideWidth()),
       buttonDispl: "block",
      }))

    }else if (this.state.currentIndex === 4){
      this.setState(prevState =>({
        currentIndex: prevState.currentIndex + 1,
        buttonTxt: "START AGAIN?",
        buttonDispl: "block",
      }))
    } else if (this.state.currentIndex > 4){
      this.setState({
        currentIndex: 0,
        translateValue: 0,
        buttonTxt: "FOR-WARD!",
      })
    }
  }
  render(){
    return(
      <div className="slider">
      <Slide translateValue={this.state.translateValue} />
      <Button nextSlide={this.nextSlide} buttonTxt={this.state.buttonTxt}/>
      <PrevButton prevSlide={this.prevSlide} buttonDispl={this.state.buttonDispl} />
       </div>
    )
  }
}

export default Slider;
