import React, { Component } from 'react';
import './App.scss';
import { library } from '@fortawesome/fontawesome-svg-core'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faBasketballBall } from '@fortawesome/free-solid-svg-icons'

library.add(faBasketballBall)

class Slider extends Component {
  constructor(props){
    super(props);
    this.state={
    currentIndex: 0,
    slides: [
      <Slide1 />,
      <Slide2/>,
      <Slide3/>,
      <Slide4/>,
      <Slide5/>
    ]
    }
     function nextSlide(){
      this.setState(prevState => ({
        currentIndex: prevState.currentIndex + 1
      }));


   }
  }
  render(){
    return(
      <div className="slider">
      <Slide/>
      <button className="fwdBtn" onClick={this.nextSlide}>FORWARD <FontAwesomeIcon icon={faBasketballBall} spin/> </button>
       </div>
    )
  }

}
const Slide = (props) => {
  return <div className="slide">
          {
          props.state.slides.map((slide, i) => (
            <Slide key={i} />
          ))
        }



</div>
}

const Slide1 = () => {
  return <div className="slide1">
  <h1>COMPARE YOURSELF</h1>
  <h1>TO THE BIGGEST</h1>
  <img></img>
  <span>STARS</span></div>
}

class Slide2 extends Component {
  render(){
return(
      <div className="slide2"></div>
    )
  }
}

class Slide3 extends Component {
  render(){
return(
      <div className="slide3"></div>
    )
  }
}

class Slide4 extends Component {
  render(){
return(
      <div className="slide4"></div>
    )
  }
}

class Slide5 extends Component {
  render(){
return(
      <div className="slide5"></div>
    )
  }
}
class App extends Component {
  render() {
    return (
     <Slider />
    );
  }
}

export default App;
