import React, { Component } from 'react';
import './scss/App.scss';

export class Slide4 extends Component {
  constructor(props){
    super(props);
    this.state={
      blocksVal: 0,
      reboundsVal: 0,
      stealsVal: 0,
    }
  }

  getBlocks=(event)=>{
    this.setState({
      blocksVal: event.target.value,
    })
    console.log(this.state.blocksVal)
  }
  getRebs=(event)=>{
    this.setState({
      reboundsVal: event.target.value,
    })
    console.log(this.state.blocksVal)
  }
  getSteals=(event)=>{
    this.setState({
      stealsVal: event.target.value,
    })
    console.log(this.state.blocksVal)
  }



  render(){
return(
      <div className="slide4"> 
      <h1 className="headline">YOUR STATS PER GAME</h1>
      <h2>DEFENCE:</h2>
      <div className="statsContainer">
      <div>
        <span>BLOCKS</span>
        <input value={this.state.blocksVal} type="number" min="0" step="1" onChange={this.getBlocks}></input>
       </div>
      <div>
        <span>REBOUNDS</span>
        <input value={this.state.reboundsVal} type="number" min="0" step="1" onChange={this.getRebs}></input>
      </div>
     <div>
       <span>STEALS</span>
       <input value={this.state.stealsVal} type="number" min="0" step="1" onChange={this.getSteals}></input>
      </div> 
     </div>
</div>
    )
  }
}

export default Slide4;
