import React, { Component } from 'react';
import bball from './images/bball';

const Loader = () =>{
    return(
        <>
            <h1 className="headline">Wait a sec...your chart is being prepared!</h1>
            <div className="loader" styles={{backgroundImage: `${bball}`}}></div>
        </>
    )
}

export default Loader;
