import React, { Component } from 'react';
import './scss/App.scss';
import butlerIMG from "./images/butler.jpg";
import curryIMG from "./images/curry.jpg";
import damianIMG from "./images/damian.jpg";
import davisIMG from"./images/davis.jpg";
import derozIMG from"./images/deroz.jpg";
import dirkIMG from "./images/dirk.jpg";
import gortatIMG from"./images/gortat.jpg";
import hardenIMG from"./images/harden.jpg";
import jjIMG from "./images/jj.jpg";
import kawhiIMG from"./images/kawhi.jpg";
import kyrieIMG from "./images/kyrie.jpg";
import lebronIMG from "./images/lebron.jpg";
import rondoIMG from "./images/rondo.jpg";
import roseIMG from "./images/rose.jpg";
import russIMG from "./images/russ.jpg";
import vinceIMG from "./images/vince.jpg";

export class Slide2 extends Component {
  constructor(props){
    super(props);
    this.state={
      players: [
        butlerIMG, curryIMG, damianIMG, davisIMG, derozIMG, dirkIMG, gortatIMG, 
        hardenIMG, jjIMG, kawhiIMG, kyrieIMG, lebronIMG, rondoIMG, roseIMG, russIMG, vinceIMG],
      names: ["Jimmy BUTLER", "Stephen CURRY", "Damian LILLARD","Anthony DAVIS", "DeMar DEROZAN", "Dirk NOVITZKY", "Marcin GORTAT",
              "James HARDEN", "JJ REDICK", "Kawhi LEONARD", "Kyrie IRVING", "LeBron JAMES", "Rajon RONDO", "Derrick ROSE", "Russel WESTBROOK", "Vince CARTER"],
      chosenPlayerID:""
    }
  }

  setID=(index)=>{
    console.log(this.state.chosenPlayerID)
    if(index===0){
    this.setState({
    chosenPlayerID: "202710"
    }) 
  } else if (index===1){
    this.setState({
      chosenPlayerID: "201939"
      }) 
  }else if (index===2){
    this.setState({
      chosenPlayerID: "203081"
      }) 
  }else if (index===3){
    this.setState({
      chosenPlayerID: "203076"
      }) 
  }else if (index===4){
    this.setState({
      chosenPlayerID: "201942"
      }) 
  }else if (index===5){
    this.setState({
      chosenPlayerID: "1717"
      }) 
  }else if (index===6){
    this.setState({
      chosenPlayerID: "101162"
      }) 
  }else if (index===7){
    this.setState({
      chosenPlayerID: "200755"
      }) 
  }else if (index===8){
    this.setState({
      chosenPlayerID: "202695"
      }) 
  }else if (index===9){
    this.setState({
      chosenPlayerID: "201939"
      }) 
  } else if (index===10){
    this.setState({
      chosenPlayerID: "202681"
      }) 
  }else if (index===11){
    this.setState({
      chosenPlayerID: "2544"
      }) 
     } else if (index===12){
    this.setState({
      chosenPlayerID: "200765"
      }) 
  }else if (index===13){
    this.setState({
      chosenPlayerID: "201565"
      }) 
  }else if (index===14){
    this.setState({
      chosenPlayerID: "201566"
      }) 
  }else if (index===15){
    this.setState({
      chosenPlayerID: "1713"
      }) 
  } else {
      this.setState({
        chosenPlayerID: ""
        }) 
    }
  }
  render(){
    return(
      <div className="slide2" chosenPlayerID={this.state.chosenPlayerID}>  
        <h1 className="headline">CHOOSE THE PLAYER:</h1>
        <div id="playersContainer">
        {
          this.state.players.map((picture, index) => {
            const ID = `player${index}`
            return (
              <div style={{backgroundImage: `url(${picture})`}} className="playerBox" key={index} id={ID} onClick={(event) => this.setID(index)}>
                <div className="hoverHeadline">
                <h2 className="playerName">{this.state.names[index]}</h2>

              </div>
              </div>
            )
          })
        }

        </div>
      </div>
    )
  }
}

export default Slide2;
